INSERT INTO `pesanan` (`id_pesanan`, `nama_barang`, `bahan_bahan`, `foto`, `cara_membuat`) VALUES (1, 'Gado Gado', '150 gr Kacang tanah (sudah digoreng)\r\n2 butir Telur rebus\r\n2 buah Tahu goreng\r\n1 papan Tempe goreng ukuran sedang\r\nBumbu Kacang\r\n4 buah cabe merah\r\n6 buah cabe rawit\r\n6 siung bawang putih\r\n5 lembar Daun jeruk\r\nSecukupnya garam (sesuai selera)\r\n100 gr gula merah\r\nSayuran pelengkap\r\n1 ikat kacang panjang rebus\r\n1 buah wortel rebus\r\n1 buah kentang rebus (ukuran sedang)\r\n1/2 bonggol sawi putih\r\nSegenggam Toge', 'gado.png', '1.Masukkan kacang yang sudah digoreng, tambahkan bumbu yang sudah ditumis terlebih dahulu, haluskan. 2.Masukkan kacang dan bumbu yang sudah dihaluskan ke dalam wajan,masukkan daun jeruk, aduk rata.\r\nTambahkan air panas aduk rata.\r\nTambahkan bumbu lainnya, gula merah, garam. Aduk rata, koreksi rata sampai bumbu kacang matang dan sisihkan. 3.Siapkan bahan sayuran\r\n\r\nGoreng tahu, tempe, dan rebus kacang panjang, kentang, telur, wortel, sawi putih. 4.Siapkan bahan sayuran, tahu, tempe dan telur, tata pada piring saji.\r\nSajikan Gado-gado saat santap bersama keluarga. Semoga bermanfaat.');
INSERT INTO `pesanan` (`id_pesanan`, `nama_barang`, `bahan_bahan`, `foto`, `cara_membuat`) VALUES (2, 'Mie Ayam', '1 kg mie basah\r\n2 iket bayam\r\nAyam kecap\r\n1/4 dada ayam\r\n5 daun jeruk\r\n1 batang serai\r\n6 siung bawang merah\r\n3 siung bawang putih\r\n1 ruas jahe\r\n2 ruas kunyit\r\n1 sdm ketumbar bubuk\r\n1 sdt lada bubuk\r\n25 gr gula merah dan secukupnya kecap manis\r\nkuah mie ayam\r\n500 ml air rebusan kaldu ayam\r\n4 siung bawang merah\r\n2 siung bawang putih\r\n1 ruas jahe\r\nirisan daun bawang\r\nsecukupnya lada, garam', 'mie.jpg', '1\r\nPisahkan daging ayam dari kulitnya, rebus daging ayam dengan dan daun salam dan serai.\r\n\r\n2\r\nHaluskan semua bumbu ayam kecap, kecuali serai dan daun jeruk\r\n\r\n3\r\nSiapkan wajan dan tuangkan minyak secukupnya untuk menumis\r\n\r\n4\r\nGoreng kulit ayam sampai garing, kemudian tumis bumbu yang sudah dihaluskan tadi, tambahkan serai dan daun jeruk\r\n\r\n5\r\nSetelah bumbunya sudah tanek, masukkan ayam yg sebelumnya sudah disuir\r\n\r\n6\r\nMasukkan gula merah dan kecap seasoning dengan garam dan penyedap\r\n\r\n7\r\nUntuk kuah mie ayam rebus air kaldu dan masukkan bawang putih, bawang merah, jahe yang sudah di geprek tambah potongan daun bawang dan seasoning lada dan garam\r\n\r\n8\r\nRebus mie dan bayam saring\r\nkemudian sajikan dengan kuah kaldu dan ayam kecap tambahkan saos cabe sesuai selera');
INSERT INTO `pesanan` (`id_pesanan`, `nama_barang`, `bahan_bahan`, `foto`, `cara_membuat`) VALUES (3, 'kfc', '1/2 kg ayam potong 6\r\n1/2 bh jeruk nipis\r\nBahan 1 :\r\n1/2 sdt ketumbar bubuk\r\n1 sdt garam menyesuaikan\r\n5 sdm tepung bumbu kasih air biar agak kentel2 gitu\r\nBahan 2 :\r\n2 bks kecil tepung bumbu sasa\r\n15 sdm tepung terigu merk sania\r\n1/2 sdt soda kue', 'kfc.jpg', '1\r\nCuci bersih ayam, kasih perasan jeruk nipis, diamkan 15 menit, cuci kembali\r\n\r\n2\r\nBumbui ayam dengan royco ayam sedikit garam dan ketumbar bubuk, klo ada baput ulek bagus juga. Masukan tepung bumbu kasih air buat jadi bahan pencelup. Marinasi 1/2 jam. 3\r\nAduk bahan 2, lalu masukan ayam ke bahan 2, tutupi di tepung, remas pelan2, lalu ketuk2 remas lagi, sampe terlihat keriting,\r\nGoreng di minyak panas dan banyak');
INSERT INTO `pesanan` (`id_pesanan`, `nama_barang`, `bahan_bahan`, `foto`, `cara_membuat`) VALUES (4, 'Pempek Palembang', 'Bahan biang :\r\n125 gr terigu\r\n15 gr garam\r\n10 gr micin\r\n500 ml air\r\n1 butir telur\r\nBahan lain :\r\n200 ml air\r\n500 gr ikan giling\r\n700 gr tapioka\r\n15 gr garam\r\n10 gr micin', 'pempek.jpg', '1\r\nAduk rata bahan biang, lalu masak sampai mendidih. Dinginkan.\r\nMasak air dalam dandang beri minyak sedikit untuk merebus pempek.\r\n\r\n2\r\nAduk rata ikan giling, micin, air dan garam sampai mengental.\r\nTambahkan bahan biang yg sudah dingin, campur rata.\r\nBeri tapioka (jangan ditekan nanti keras)\r\nCukup aduk balik sampai rata dengan tangan\r\n\r\n3\r\nBentuk adonan dengan tangan yg sudah dibaluri tapioka.\r\nBeri kocokan telur, masak sampai mengapung lalu diamkan 5 menit baru angkat.\r\n\r\n4\r\nDinginkan pempek lalu goreng. sajikan');
INSERT INTO `pesanan` (`id_pesanan`, `nama_barang`, `bahan_bahan`, `foto`, `cara_membuat`) VALUES (5, 'Tahu Tek', '2 buah tahu putih\r\n1 buah kentang\r\n1 genggam taoge\r\n3 butir telur\r\n1 buah timun ukuran kecil\r\n1 sdm petis\r\nSecukupnya garam dan gula\r\n2 sdm kecap manis\r\n1 siung bawang putih\r\n4 sdm kacang tanah goreng\r\n2 buah cabe rawit (sesuai selera)\r\nSecukupnya air matang\r\nSecukupnya kerupuk\r\nSesuai selera lontong\r\nSecukupnya bawang goreng', 'tahu tek.jpeg', '1\r\nPotong tahu menjadi beberapa bagian dan goreng hingga matang. 2\r\nKupas dan potong kentang kecil-kecil, goreng hingga matang\r\n\r\n3\r\nBersihkan taoge, blansir\r\n\r\n4\r\nKocok telur, beri sedikit garam, goreng hingga matang\r\n\r\n5\r\nIris timun kecil-kecil panjang (seperti acar) 6\r\nHaluskan kacang tanah, bawang putih, cabe, gula, dan garam. Jika sudah halus masukkan petis dan ulek hingga tercampur rata 7\r\nMasukkan sedikit air, ulek lagi hingga tercampur rata\r\n\r\n8\r\nTambahkan kecap manis, campur hingga rata. Koreksi rasa. 9\r\nUntuk penyajian:\r\n- Siapkan piring\r\n- Tata lontong yang sudah dipotong\r\n- Potong tahu goreng menggunakan garpu dan gunting\r\n- Potong telur dadar menggunakan garpu dan gunting\r\n- tambahkan kentang goreng\r\n- beri taoge dan timun\r\n- Siram dengan bumbu kacang\r\n- Taburi bawang goreng (bisa ditambahkan irisan seledri juga, saya skip karena tidak suka seledri)\r\n- Tambahkan kerupuk diatasnya');
INSERT INTO `pesanan` (`id_pesanan`, `nama_barang`, `bahan_bahan`, `foto`, `cara_membuat`) VALUES (6, 'Bakso', 'Bahan 1 :\r\n500 gram Daging\r\n1/2 sdm garam kasar\r\n1 bks kecil kaldu sapi / ayam\r\n3 siung bawang putih\r\n3 sdm bawang putih goreng\r\n1 sdt lada bubuk\r\nBahan 2 ;\r\n200 gram es batu\r\n2 butir putih telur\r\n1/4 sdm baking powder\r\nBahan 3:\r\n100 gram tepung tapioka', 'bakso.jpg', '1\r\nMasukan semua bahan 1 ke dalam chopper, chopper hingga daging setengah halus\r\n\r\n2\r\nKemudian masukan bahan ke 2 chopper hingga halus.\r\n\r\n3\r\nDan terakhir masukan tepung tapioka.chopper lagi hingga merata.\r\n\r\n4\r\nRebus air hingga mendidih kemudian matikan kompor,\r\nAmbil adonan pencet mengunakan tangan membentuk bulatan bakso, kemudian masukan kedalam air panas tadi. 5\r\nSetelah semua adonan mengapung, pindahkan ke air dingin biar baksonya kenyal 6\r\nKemudian tiriskan..bakso siap');
